import{_ as e,c as a,o as n,a as t}from"./app.ecf32480.js";const i="/sunbeam/assets/github.31dcffba.gif",b=JSON.parse('{"title":"Introduction","description":"","frontmatter":{},"headers":[{"level":2,"title":"What is sunbeam?","slug":"what-is-sunbeam","link":"#what-is-sunbeam","children":[]},{"level":2,"title":"Why sunbeam?","slug":"why-sunbeam","link":"#why-sunbeam","children":[]}],"relativePath":"user-guide/index.md"}'),s={name:"user-guide/index.md"},o=t('<h1 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-hidden="true">#</a></h1><h2 id="what-is-sunbeam" tabindex="-1">What is sunbeam? <a class="header-anchor" href="#what-is-sunbeam" aria-hidden="true">#</a></h2><p>Sunbeam is a command line launcher, similar to spotlight, rofi or alfred.</p><p>It allows you to build interactives UIs from simple scripts, and share them on the web.</p><p><img src="'+i+'" alt="A demo of the github extension"></p><h2 id="why-sunbeam" tabindex="-1">Why sunbeam? <a class="header-anchor" href="#why-sunbeam" aria-hidden="true">#</a></h2><p>I love TUIs, but I spend way to much time effort them. I used a lot of application launchers, but all of them had some limitations.</p><p>Sunbeam supports:</p><ul><li>Running on all platforms (Windows, Linux, MacOS)</li><li>Extensions written in any language, as long as they can output JSON</li><li>Generating complex UIs compose of list, forms, details...</li><li>Running as an headless server on a remote machine</li></ul><p>Sunbeam can run from any terminal, but it also comes with an optional GUI for a launcher-like experience.</p>',10),r=[o];function l(u,h,c,d,m,p){return n(),a("div",null,r)}const f=e(s,[["render",l]]);export{b as __pageData,f as default};
